wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(1247920594575839056)
,p_process_sequence=>10
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Load Collection'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'   l_question_nbr  number := 0;',
'   l_vc_arr2       apex_application_global.vc_arr2; ',
'   l_answers       varchar2(32767);',
'   l_answer_max    number;',
'begin',
'',
'   apex_collection.create_or_truncate_collection (p_collection_name => ''SB_RESPONSES'');',
'',
'   -- load collection with respondents',
'   for c1 in (',
'      select survey_id, id, started_date, completed_date, request_email_sent, ',
'             reminder_email_sent, thanks_email_sent, duration_mins',
'        from eba_sb_responses',
'       where survey_id = :ACTIVE_SURVEY_ID',
'         and started_date is not null )',
'   loop',
'      apex_collection.add_member (',
'         p_collection_name => ''SB_RESPONSES'',',
'         p_n001            => c1.survey_id,',
'         p_c001            => c1.id,',
'         p_d001            => c1.started_date,',
'         p_d002            => c1.completed_date,',
'         p_d003            => c1.request_email_sent,',
'         p_d004            => c1.reminder_email_sent,',
'         p_d005            => c1.thanks_email_sent,',
'         p_n002            => c1.duration_mins );',
'      commit;',
'   end loop;',
'',
'',
'   -- select each question, then each respondent, then all their answers to that question',
'   for c1 in (',
'      select q.question_number',
'        from eba_sb_sections s, eba_sb_questions q, eba_sb_questionnaires qr',
'       where s.id = q.section_id',
'         and q.questionnaire_id = qr.id',
'         and qr.survey_id = :ACTIVE_SURVEY_ID',
'         and q.exclude = ''N''',
'       order by section_seq, question_seq )',
'   loop',
'      l_question_nbr := l_question_nbr + 1;',
'',
'      if l_question_nbr = 50 then',
'         exit;',
'      else',
'         for c2 in (',
'            select c001 response_id, seq_id',
'              from apex_collections c',
'             where n001 = :ACTIVE_SURVEY_ID',
'               and collection_name = ''SB_RESPONSES'' )',
'         loop',
'            if nvl(:P7_DECODE_ANSWERS,''N'') = ''N'' then',
'               select nvl(value_text,value)',
'                 bulk collect ',
'                 into l_vc_arr2 ',
'                 from eba_sb_question_responses',
'                where response_id = c2.response_id',
'                  and question_number = c1.question_number',
'                order by value asc nulls last, value_text; ',
'            else',
'               select case when q.type in (''RANGE'',''SINGLE'',''MULTIPLE'') and ',
'                                r.value = ''0'' and',
'                                q.na_label is not null then',
'                                q.na_label',
'                           when r.value_text is null then  ',
'                                nvl((select a.text',
'                                       from eba_sb_answers a',
'                                      where q.id = a.question_id',
'                                        and a.value =  r.value),',
'                                     r.value)',
'                           else r.value_text',
'                           end',
'                 bulk collect ',
'                 into l_vc_arr2 ',
'                 from eba_sb_question_responses r,',
'                      eba_sb_questions q',
'                where q.questionnaire_id = :P6_QUESTIONNAIRE_ID',
'                  and q.question_number = r.question_number',
'                  and r.response_id = c2.response_id',
'                  and r.question_number = c1.question_number',
'                order by r.value asc nulls last, r.value_text;',
'            end if;',
'            l_answers := apex_util.table_to_string ( ',
'                            p_table => l_vc_arr2, ',
'                            p_string => '', ''); ',
'',
'           if :P7_LONG_ANSWERS = ''Y'' then',
'              l_answer_max := 3997;',
'           else',
'              l_answer_max := 47;',
'           end if;',
'',
'           if length(l_answers) > l_answer_max then',
'              l_answers := substr(l_answers,1,l_answer_max)||''...'';',
'           end if;',
'',
'            apex_collection.update_member_attribute (',
'               p_collection_name => ''SB_RESPONSES'',',
'               p_seq             => c2.seq_id,',
'               p_attr_number     => l_question_nbr + 1,',
'               p_attr_value      => l_answers );',
'            commit;',
'         end loop;',
'      end if;',
'   end loop;',
'',
'   :P7_QUESTION_CNT := l_question_nbr;',
'',
'end;'))
,p_process_error_message=>'Failed to load collection.'
);
end;
