wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(1219014695708479131)
,p_process_sequence=>20
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Import'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_data        blob;',
'  l_xml_data    clob;',
'  l_mimetype    wwv_flow_files.mime_type%type;',
'  l_charset     wwv_flow_files.file_charset%type;',
'  l_src_offset  integer := 1;',
'  l_dst_offset  integer := 1;',
'  l_warn        integer;',
'  l_lang_ctx    integer := dbms_lob.default_lang_ctx;',
'',
'begin',
'  -- Get at the uploaded data',
'  begin',
'    update wwv_flow_files set file_type = ''EBA_SB_SURVEY_XML''',
'      where name = :P21_IMPORT_FILE;',
'    select f.blob_content,',
'           f.mime_type,',
'           nvl(f.file_charset, f.dad_charset)',
'      into l_data,',
'           l_mimetype,',
'           l_charset',
'      from wwv_flow_files f',
'      where f.name = :P21_IMPORT_FILE;',
'  exception',
'    when no_data_found then',
'      raise_application_error(-20001, apex_lang.message(''UPLOAD_FAILED''));',
'  end;',
'  delete from wwv_flow_files where name = :P21_IMPORT_FILE;',
'',
'  sys.dbms_lob.createtemporary(l_xml_data, false, sys.dbms_lob.session);',
'  dbms_lob.converttoclob(',
'    l_xml_data,',
'    l_data,',
'    dbms_lob.lobmaxsize,',
'    l_dst_offset,',
'    l_src_offset,',
'    NLS_CHARSET_ID(''UTF8''),',
'    l_lang_ctx,',
'    l_warn);',
'  eba_sb.import_survey(',
'        p_xml_data     => l_xml_data,',
'        p_force_design => case when :P21_FORCE_DESIGN = ''Y'' then true else false end,',
'        p_new_name     => :P21_NEW_NAME,',
'        p_keep_row_key => case when :P21_KEEP_ROW_KEY = ''Y'' then true else false end );',
'  sys.dbms_lob.freetemporary(l_xml_data);',
'  :P21_IMPORT_FILE  := null;',
'exception when others then',
'    rollback;',
'    delete from wwv_flow_files where name = :P21_IMPORT_FILE;',
'    commit;',
'    :P21_IMPORT_FILE := null;',
'    raise_application_error(-20001, apex_lang.message(''XML_PARSE_FAILED''));',
'end;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(1218923702990571254)
