,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Add Respondents'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_to_community_id    number;',
'    l_from_community_id  number;',
'    l_respondent_id      number;',
'    l_in_community       varchar2(1);',
'begin',
'    if :P58_COMMUNITY_ACTION in (''-2'',''0'') and :P58_COMMUNITY_NAME is not null then',
'        -- Creating New Community',
'        insert into eba_sb_resp_community',
'        (name)',
'        values',
'        (:P58_COMMUNITY_NAME) returning id into l_to_community_id;',
'    end if;',
'',
'    if :P58_COMMUNITY_ACTION = ''-1'' and :P58_COMMUNITY_ID_TO is not null then',
'        -- Copying Community to another community',
'        l_from_community_id := :P58_COMMUNITY_ID;',
'        l_to_community_id   := :P58_COMMUNITY_ID_TO;',
'    else',
'        -- Copying Community to a new community',
'        l_from_community_id := :P58_COMMUNITY_ID;',
'    end if;',
'',
'    if :P58_COMMUNITY_ACTION in (''-1'',''0'') then',
'        -- Copy Respondents',
'        insert into eba_sb_resp_comm_ref (respondent_id, community_id)',
'        select',
'            rsp.id             as respondent_id,',
'            l_to_community_id  as community_id',
'        from',
'            EBA_SB_resp_comm_ref rcl,',
'            EBA_SB_respondents rsp,',
'            EBA_SB_resp_community rc',
'        where',
'            rsp.id = rcl.respondent_id',
'        and',
'            rc.id = rcl.community_id',
'        and',
'            rcl.community_id = l_from_community_id;',
'    end if;',
'',
'    if :P58_COMMUNITY_ACTION = -3 then',
'        -- Add Respondent',
'        insert into eba_sb_resp_comm_ref',
'        (respondent_id, community_id)',
'        values',
'        (:P58_RESPONDENT_ID, :P58_COMMUNITY_ID_TO);',
'    end if;',
'',
'    -- Dont do this for adding a single respondent to an existing community',
'    if :P58_COMMUNITY_ACTION != -3 then',
'        -- Handle Additional Respondents for entire communities',
'        for c in',
'        (',
'            select',
'                c001 as respondent',
'            from',
'                apex_collections',
'            where',
'                collection_name = ''EBA_SB_RESPONDENT_VALID''',
'        )',
'        loop',
'            begin',
'                select decode(r.id, null, ''N'', ''Y'')',
'                  into l_in_community',
'                  from EBA_SB_respondents r,',
'                       EBA_SB_resp_comm_ref ref',
'                 where upper(r.email) = upper(c.respondent)',
'                   and r.id = ref.respondent_id',
'                   and ref.community_id = l_to_community_id;',
'            exception',
'                when no_data_found then',
'                    l_in_community := ''N'';',
'            end;',
'',
'            begin',
'                select r.id',
'                  into l_respondent_id',
'                  from EBA_SB_respondents r',
'                 where upper(r.email) = upper(c.respondent);',
'            exception',
'                when no_data_found then',
'                    l_respondent_id := null;',
'            end;',
'',
'            if l_respondent_id is null then',
'                insert into eba_sb_respondents',
'                (email)',
'                values',
'                (c.respondent) returning id into l_respondent_id;',
'            end if;',
'',
'            if l_in_community = ''N'' and :P58_COMMUNITY_ACTION != -5 then',
'                insert into eba_sb_resp_comm_ref',
'                (respondent_id, community_id)',
'                values',
'                (l_respondent_id, nvl(l_to_community_id,:P58_COMMUNITY_ID_TO));',
'            end if;',
'        end loop;',
'    end if;',
'',
'    ---------------------',
'    -- delete collections',
'    --',
'    wwv_flow_collection.DELETE_COLLECTION(''EBA_SB_RESPONDENT_INVALID'');',
'    wwv_flow_collection.DELETE_COLLECTION(''EBA_SB_RESPONDENT_VALID'');',
'    wwv_flow_collection.DELETE_COLLECTION(''EBA_SB_RESPONDENT_CREATE'');',
'end;'))
,p_process_error_message=>'#SQLERRM#'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(1593758182072602823)
,p_process_success_message=>'Community information stored successfully.'
);
