wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(1206624710451308328)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Create Survey'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_survey_id         eba_sb_surveys.id%type;',
'  l_survey_row_key    eba_sb_surveys.row_key%type;',
'  l_old_survey_id     eba_sb_surveys.id%type;',
'  l_questionnaire_id  number;',
'',
'  l_data        blob;',
'  l_text_data   clob;',
'  l_mimetype    wwv_flow_files.mime_type%type;',
'  l_charset     wwv_flow_files.file_charset%type;',
'  l_src_offset  integer := 1;',
'  l_dst_offset  integer := 1;',
'  l_warn        integer;',
'  l_lang_ctx    integer := dbms_lob.default_lang_ctx;',
'begin',
'  insert into eba_sb_surveys (name, type, tags, target_respondents, target_date, target_percent, auto_complete, mins_to_complete)',
'    values (:P61_NAME, :P61_TYPE, :P62_TAGS, :P62_TARGET_RESPONDENTS, to_date(:P62_TARGET_DATE,:DATE_FORMAT), :P62_TARGET_PERCENT, :P62_AUTO_COMPLETE, :P62_MINS_TO_COMPLETE)',
'    returning id, row_key into l_survey_id, l_survey_row_key;',
'  if :P60_CREATE_FROM = 2 and :P60_FROM_TEMPLATE_Q_ID is not null then',
'    l_questionnaire_id := eba_sb.copy_questionnaire(',
'        p_questionnaire_id => to_number(:P60_FROM_TEMPLATE_Q_ID),',
'        p_new_title => :P62_TITLE,',
'        p_dst_survey_id => l_survey_id,',
'        p_survey_row_key => l_survey_row_key);',
'  elsif :P60_CREATE_FROM = 3 and :P60_FROM_SURVEY_Q_ID is not null then',
'    l_questionnaire_id := eba_sb.copy_questionnaire(',
'        p_questionnaire_id => to_number(:P60_FROM_SURVEY_Q_ID),',
'        p_new_title => :P62_TITLE,',
'        p_dst_survey_id => l_survey_id,',
'        p_survey_row_key => l_survey_row_key);',
'    select s.id into l_old_survey_id from eba_sb_questionnaires q, eba_sb_surveys s where s.id = q.survey_id and q.id = :P60_FROM_SURVEY_Q_ID;',
'    -- copy emails also',
'    insert into eba_sb_emails(survey_id, name, type, is_current, subject, message, send_after_days)',
'        select l_survey_id, name, type, is_current, subject, message, send_after_days',
'            from eba_sb_emails where survey_id = l_old_survey_id;',
'  elsif :P60_CREATE_FROM = 4 then',
'    if :P62_INPUT_SOURCE = 1 then',
'      -- Get at the uploaded data',
'      begin',
'        update wwv_flow_files set file_type = ''EBA_SB_SURVEY_TXT''',
'          where name = :P62_INPUT_FILE;',
'        select f.blob_content,',
'               f.mime_type,',
'               nvl(f.file_charset, f.dad_charset)',
'          into l_data,',
'               l_mimetype,',
'               l_charset',
'          from wwv_flow_files f',
'          where f.name = :P62_INPUT_FILE;',
'      exception',
'        when no_data_found then',
'          raise_application_error(-20001, apex_lang.message(''UPLOAD_FAILED''));',
'      end;',
'      delete from wwv_flow_files where name = :P62_INPUT_FILE;',
'',
'      sys.dbms_lob.createtemporary(l_text_data, false, sys.dbms_lob.session);',
'      dbms_lob.converttoclob(',
'        l_text_data,',
'        l_data,',
'        dbms_lob.lobmaxsize,',
'        l_dst_offset,',
'        l_src_offset,',
'        NLS_CHARSET_ID(''UTF8''),',
'        l_lang_ctx,',
'        l_warn);',
'    else',
'        l_text_data := to_clob(:P62_INPUT_TEXT);',
