 p_id=>wwv_flow_api.id(1594700405556514097)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Send Invitation Email'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'   l_subs        eba_sb_email_api.temp_subs_t;',
'   l_body        clob;',
'   l_body_html   clob;',
'   l_q_row_key   varchar2(10);',
'   l_mins        number;',
'   l_response_id varchar2(30);',
'   l_list        varchar2(32767);',
'begin',
'',
'    begin',
'        select',
'            row_key',
'        into',
'            l_q_row_key',
'        from',
'            eba_sb_questionnaires',
'        where',
'            id = :P82_QUESTIONNAIRE_ID;',
'    exception',
'        when no_data_found then',
'            l_q_row_key := null;',
'    end;',
'    begin',
'        select',
'            mins_to_complete',
'        into',
'            l_mins',
'        from',
'            eba_sb_surveys',
'        where',
'            id = :P82_SURVEY_ID;',
'    exception',
'        when no_data_found then',
'            l_mins := 5;',
'    end;',
'',
'    -- Create the respondent list of email addresses',
'    for x in',
'    (',
'        select',
'            r.email',
'        from',
'            eba_sb_respondents r,',
'            eba_sb_resp_community c,',
'            eba_sb_resp_comm_ref rc',
'        where',
'            c.id = :P82_COMMUNITY_ID',
'        and',
'            rc.community_id = c.id',
'        and',
'            r.id = rc.respondent_id',
'        and',
'            r.id not in (select respondent_id from eba_sb_email_opt_out)',
'    )',
'    loop',
'        l_list := l_list || x.email || chr(13) || chr(10);',
'    end loop;',
'    l_list := rtrim(l_list, chr(13) || chr(10));',
'',
'    -- Add the respondent record for each respondent',
'    eba_sb.add_responses(:P82_SURVEY_ID, 1, l_list);',
'',
'    -- Send the emails',
'    for c in',
'    (',
'        select',
'            r.id as respondent_id,',
'            r.name,',
'            r.email',
'        from',
'            eba_sb_respondents r,',
'            eba_sb_resp_community c,',
'            eba_sb_resp_comm_ref rc',
'        where',
'            c.id = :P82_COMMUNITY_ID',
'        and',
'            rc.community_id = c.id',
'        and',
'            r.id = rc.respondent_id',
'        and',
'            r.id not in (select respondent_id from eba_sb_email_opt_out)',
'    )',
'    loop',
'        begin',
'            select id',
'              into l_response_id',
'              from eba_sb_responses',
'             where email_addr = c.email',
'               and survey_id = :P82_SURVEY_ID',
'               and delivered = ''N'';',
'        exception',
'            when no_data_found then',
'                l_response_id := eba_sb.add_response(:P82_SURVEY_ID);',
'        end;',
'        l_subs(''#URL#'')               := apex_util.host_url(''SCRIPT'') || ',
'                                         ''f?p=''||:APP_ID||'':Q:0:''||l_q_row_key||'':::code:'' || l_response_id;',
'        l_subs(''#SURVEY_NAME#'')       := :P82_SURVEY_NAME;',
'        l_subs(''#NUM_MINUTES#'')       := l_mins;',
'        l_subs(''#APPLICATION_TITLE#'') := nvl(eba_sb_fw.get_preference_value(''APPLICATION_TITLE''),:APP_NAME);',
'        l_subs(''#EMAIL_FOOTER_TEXT#'') := replace(eba_sb_fw.get_preference_value(''EMAIL_FOOTER''),''N/A'',null);',
'        l_subs(''#OPT_OUT_TEXT#'')      := apex_lang.message(''OPT_OUT_TEXT'', ',
'                                             apex_util.host_url(''SCRIPT'') || ''f?p='' || v(''APP_ID'') || '':optout:0::NO::P68_PERSON_ID:'' || c.respondent_id || '':'',',
'                                             :APPLICATION_TITLE);',
'',
'        eba_sb_email_api.send (',
'            p_template_id   => :P82_EMAIL_TEMPLATE_ID,',
'            p_subs          => l_subs,',
'            p_to            => c.email,',
'            p_from          => eba_sb_fw.get_preference_value(''EMAIL_FROM''),',
'            p_replyto       => nvl(eba_sb_fw.get_preference_value(''EMAIL_REPLY_TO''),null),',
'            p_log           => true,',
'            p_survey_id     => :P82_SURVEY_ID,',
'            p_community_id  => :P82_COMMUNITY_ID,',
'            p_respondent_id => c.respondent_id',
'        );',
'',
'        if l_response_id is not null then',
'            update eba_sb_responses set delivered = ''Y'', request_email_sent = localtimestamp where id = l_response_id;',
'        end if;',
'    end loop;',
'end;'))
,p_process_error_message=>'#SQLERRM#'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(1594698380624514078)
,p_process_success_message=>'Invitation emails sent successfully.'
);
end;
