wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(1598003289256376566)
,p_process_sequence=>20
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Queue Reminder Email'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_subject     eba_sb_email_templates.default_email_subject%type;',
'    l_message     clob;',
'    l_mins        number;',
'    l_row_key     varchar2(10);',
'    l_header      eba_sb_email_styles.template_header%type;',
'    l_body        eba_sb_email_templates.template_body%type;',
'    l_footer      eba_sb_email_styles.template_footer%type;',
'    l_response_id varchar2(30);',
'begin',
'    l_response_id := eba_sb.add_response(:P82_SURVEY_ID);',
'    -- Populate the row key (which will be used in the survey url construction below)',
'    begin',
'        select',
'            row_key',
'        into',
'            l_row_key',
'        from',
'            eba_sb_questionnaires',
'        where',
'            id = :P82_QUESTIONNAIRE_ID;',
'    exception',
'        when no_data_found then',
'            l_row_key := null;',
'    end;',
'',
'    -- Populate the email body''s header and footer text',
'    begin',
'        select',
'            template_header,',
'            template_footer',
'        into',
'            l_header,',
'            l_footer',
'        from',
'            eba_sb_email_styles',
'        where',
'            style_name = ''Red Style'';',
'    exception',
'        when no_data_found then',
'            l_header := null;',
'            l_footer := null;',
'    end;',
'',
'    -- Populate the email body text',
'    begin',
'        select',
'            default_email_subject,',
'            template_body',
'        into',
'            l_subject,',
'            l_body',
'        from',
'            eba_sb_email_templates',
'        where',
'            template_type = ''REMINDER'';',
'    exception',
'        when no_data_found then',
'            l_subject := ''Survey Reminder Email'';',
'            l_body := null;',
'    end;',
'',
'    -- Populate the email body text token replacement values',
'    begin',
'        select',
'            mins_to_complete',
'        into',
'            l_mins',
'        from',
'            eba_sb_surveys',
'        where',
'            id = :P82_SURVEY_ID;',
'    exception',
'        when no_data_found then',
'            l_mins := 5;',
'    end;',
'',
