'  end if;',
'',
'  l_content_length := sys.dbms_lob.getlength(l_page_content);',
'',
'  sys.owa_util.mime_header(''text/html'', false, ''utf-8'');',
'  sys.htp.p(''Content-Length: '' || l_content_length);',
'  sys.htp.p(''Content-Language: '' || l_lang);',
