 p_id=>wwv_flow_api.id(3325114988405202050)
,p_process_sequence=>10
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'results'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  c_lf                varchar2(1) := chr(10);',
'  c_cr                varchar2(1) := chr(13);',
'  l_response_id       eba_sb_responses.id%type := null;',
'  l_completed_date    eba_sb_responses.completed_date%type;',
'  l_survey_id         eba_sb_surveys.id%type := null;',
'  l_survey_state      eba_sb_surveys.state%type := null;',
'  l_survey_type       eba_sb_surveys.type%type := null;',
'  l_questionnaire_id  eba_sb_questionnaires.id%type;',
'  l_q_id              eba_sb_questionnaires.row_key%type;',
'  l_value             eba_sb_question_responses.value%type;',
'  l_value_text        eba_sb_question_responses.value_text%type;',
'  l_question_values   wwv_flow_global.vc_arr2;',
'  l_question_types    wwv_flow_global.vc_arr2;',
'  l_answers           wwv_flow_global.vc_arr2;',
'  l_answer_found      number;',
'  l_na_answer         boolean;',
'  l_ndx1              number;',
'  l_ndx2              number;',
'  l_skip_src          varchar(255);',
'  l_skip_op           varchar(4);',
'  l_skip_answer       varchar(255);',
'',
'  procedure sendResponse(status varchar2 default ''OK'',',
'                        message varchar2 default '''') is',
'  begin',
'    apex_plugin_util.print_json_http_header;',
'    sys.htp.p(''{'' ||',
'      apex_javascript.add_attribute(''status'', status) ||',
'      apex_javascript.add_attribute(''message'', message, false, false) || ''}'');',
'  end;',
'',
'begin',
'  wwv_flow.debug(''Survey questionnaire results: Questionnaire ID='' || :P99_Q_ID || '', Response ID='' || :P99_RESPONSE_ID);',
'  wwv_flow.debug(''Survey questionnaire results received ''||',
'    apex_application.g_f02.count || '' answers for '' ||',
'    apex_application.g_f01.count || '' questions'');',
'',
'  for c1 in (',
'      select r.id, r.completed_date, s.id survey_id, s.state, s.type, q.id questionnaire_id, q.row_key',
'        from eba_sb_surveys s join eba_sb_questionnaires q on s.id = q.survey_id left join eba_sb_responses r on s.id = r.survey_id',
'        where q.row_key = :P99_Q_ID and (r.id = :P99_RESPONSE_ID or :P99_RESPONSE_ID is null)) loop',
'    l_response_id := c1.id;',
'    l_completed_date := c1.completed_date;',
'    l_survey_id := c1.survey_id;',
'    l_survey_state := c1.state;',
'    l_survey_type := c1.type;',
'    l_questionnaire_id := c1.questionnaire_id;',
'    l_q_id := c1.row_key;',
'    exit; -- when response id is null (self-select survey) there could be more than one result',
'  end loop;',
'',
'  if :P99_RESPONSE_ID is null then',
'    l_response_id := null;',
'  end if;',
'',
'  if l_response_id is null and l_survey_id is not null and l_survey_state in (''TEST'', ''ACTIVE'') and l_survey_type = ''SELF-SELECT'' then',
'    -- this is a response without a response id for a self-select survey so create a response now',
'    -- this is unlikely because the start request should have already created one',
'    l_response_id := eba_sb.add_response(l_survey_id);',
'    l_completed_date := null;',
'  end if;',
'',
'  if (l_response_id is null or l_q_id is null or l_survey_id is null) and (:P99_RESPONSE_ID != ''preview'' or :P99_RESPONSE_ID is null) then',
'    -- a response has come in for a response that doesn''t exist or a questionnaire/survey that doesn''t exist',
'    -- or the response and questionnaire exist but are not related to each other.',
'    sendResponse(''FAIL'', apex_lang.message(''INVALID_SURVEY_REQUEST''));',
'    return;',
'  end if;',
'',
'  if l_completed_date is not null then',
'    -- someone is trying to respond to a survey twice',
'    sendResponse(''FAIL'', apex_lang.message(''SURVEY_ALREADY_COMPLETED''));',
'    return;',
'  end if;',
'',
'  if l_survey_state not in (''TEST'', ''ACTIVE'') then',
'    sendResponse(''FAIL'', apex_lang.message(''SURVEY_EXPIRED''));',
'    return;',
'  end if;',
'',
'  -- Validation',
'  -- first map question numbers to values',
'  for i in 1..apex_application.g_f01.count loop',
'      if l_question_values.exists(apex_application.g_f01(i)) then',
'        l_question_values(apex_application.g_f01(i)) := l_question_values(apex_application.g_f01(i)) || c_cr || apex_application.g_f02(i);',
'      else',
'        l_question_values(apex_application.g_f01(i)) := apex_application.g_f02(i);',
'      end if;',
'  end loop;',
' ',
'  -- for each question validate answer(s)',
'  for c2 in (select id, question, question_number, type, min, max, required, skip_condition, ',
'               case when other_label is null then ''N'' else ''Y'' end other, case when na_label is null then ''N'' else ''Y'' end na',
'             from eba_sb_questions ',
'             where questionnaire_id = l_questionnaire_id and exclude=''N'') loop',
'',
'    -- first check if this question is needs to be skipped',
'    if c2.skip_condition is not null then',
'      l_ndx1 := instr(c2.skip_condition, '' '', 1, 1);',
'      l_ndx2 := instr(c2.skip_condition, '' '', 1, 2);',
'      if l_ndx1 > 0 and l_ndx2 > 0 then',
'        l_skip_src := substr(c2.skip_condition, 1, l_ndx1 - 1);',
'        l_skip_op := substr(c2.skip_condition, l_ndx1 + 1, 2);',
'        l_skip_answer := to_number(substr(c2.skip_condition, l_ndx2 + 1));',
'        begin',
'            l_value := to_number(l_question_values(l_skip_src));',
'        exception when value_error then',
'            l_value := null;',
'        end;',
'        if l_skip_op = ''=='' then',
'           continue when l_value = l_skip_answer;',
'        else',
'           continue when l_value != l_skip_answer;',
'        end if;',
'      end if;',
'    end if;',
'',
'    -- get the answer in text and number form as appropriate',
'    begin',
'      l_value_text := l_question_values(c2.question_number);',
'    exception when no_data_found then',
'        l_value_text := null;',
'    end;',
'    begin',
'      l_value := to_number(l_value_text);',
'    exception',
'        when value_error then',
'          l_value := null;',
'    end;',
'',
'    l_question_types(c2.question_number) := c2.type; -- remember type for updating answers below',
'',
'    if l_value_text is null then',
'      if c2.required = ''Y'' then',
'        sendResponse(''FAIL'', apex_lang.message(''VALIDATION_VALUE_REQUIRED'', c2.question));',
'        return;',
'      end if;',
'    else',
'      case',
'      when c2.type = ''NUMBER'' then',
'        if l_value is null or (c2.min is not null and l_value < c2.min) or c2.max is not null and l_value > c2.max then',
'          sendResponse(''FAIL'',  apex_lang.message(''VALIDATION_NUMBER_RANGE'', c2.question));',
'          return;',
'        end if;',
'      when c2.type in (''OPEN'', ''TEXT'') then',
'        if (c2.max is not null and length(l_value_text) > c2.max) or (c2.min is not null and length(l_value_text) < c2.min) then',
'          sendResponse(''FAIL'',  apex_lang.message(''VALIDATION_TEXT_LENGTH'', c2.question));',
'          return;',
'        end if;',
'      when c2.type in (''RANGE'', ''SINGLE'') then',
'        select count(*) into l_answer_found',
'          from eba_sb_answers ',
'          where question_id = c2.id and value = l_value;',
'        if not (l_answer_found = 1 or (c2.na = ''Y'' and l_value is not null and l_value = 0) or (c2.other = ''Y'' and l_value is null)) then',
'          sendResponse(''FAIL'', apex_lang.message(''VALIDATION_BAD_ANSWER'', c2.question));',
'          return;',
'        end if;',
'      when c2.type = ''MULTIPLE'' then',
'        -- check each answer',
'        l_na_answer := false;',
'        l_answers := apex_util.string_to_table(l_value_text, c_cr);',
'        for j in 1..l_answers.count loop',
'          l_value_text := l_answers(j);',
'          begin',
'            l_value := to_number(l_value_text);',
'          exception',
'            when value_error then',
'              l_value := null;',
'          end;',
'          select count(*) into l_answer_found ',
'            from eba_sb_answers',
'            where question_id = c2.id and value = l_value;',
'          if not (l_answer_found = 1 or (c2.na = ''Y'' and l_value is not null and l_value = 0) or (c2.other = ''Y'' and l_value is null)) then',
'            sendResponse(''FAIL'', apex_lang.message(''VALIDATION_BAD_ANSWER'', c2.question));',
'            return;',
'          end if;',
'          if c2.na = ''Y'' and l_value = 0 then',
'            if l_answers.count != 1 then',
'              sendResponse(''FAIL'', apex_lang.message(''VALIDATION_INCONSISTENT_ANSWERS'', c2.question));',
'              return;',
'            else',
'              l_na_answer := true;',
'            end if;',
'          end if;',
'        end loop;',
'',
'        if not l_na_answer then',
'          if (c2.min is not null and l_answers.count < c2.min) or (c2.max is not null and l_answers.count > c2.max) then',
'            sendResponse(''FAIL'', apex_lang.message(''VALIDATION_ANSWER_COUNT'', c2.question));',
'            return;',
'          end if;',
'        end if;',
'      else',
'        null;',
'      end case;',
'    end if;',
'  end loop;',
'',
'  if :P99_RESPONSE_ID = ''preview'' then',
'    sendResponse(''NOACTION'', apex_lang.message(''PREVIEW_NOACTION''));',
'    return;',
'  end if;',
'',
'  -- update response',
'  update eba_sb_responses',
'    set completed_date = localtimestamp,',
'      last_question = null',
'    where id = l_response_id;',
'',
'  -- add/update all the answers',
'  delete from eba_sb_question_responses where response_id = l_response_id;',
'  for i in 1..apex_application.g_f01.count loop',
'    l_value := null;',
'    l_value_text := null;',
'    -- this is the point to detect no such question but its not worth doing exception handled below',
'    if l_question_types(apex_application.g_f01(i)) in (''OPEN'', ''TEXT'') then',
'      l_value_text := apex_application.g_f02(i);',
'    elsif substr(apex_application.g_f02(i), 1, 6) = ''other'' || c_lf then',
'      -- other choices are marked with a special prefix to make sure they are never treated as a number',
'      l_value_text := substr(apex_application.g_f02(i), 6);',
'    else',
'      begin',
'        l_value := to_number(apex_application.g_f02(i));',
'      exception',
'        when value_error then',
'          l_value_text := apex_application.g_f02(i);',
'      end;',
'    end if;',
'    insert into eba_sb_question_responses (response_id, question_number, value, value_text)',
'      values (l_response_id, apex_application.g_f01(i), l_value, l_value_text);',
'  end loop;',
'',
'  -- check for thank you email to send',
'  eba_sb.survey_job(p_app_id => :APP_ID, p_email_only => true);',
'  sendResponse();',
'',
'exception ',
'  when others then',
'    sendResponse(''FAIL'', ''Internal error: '' || SQLERRM);',
'    raise;',
'end;'))
);
