'declare',
'  l_response_id     eba_sb_responses.id%type := null;',
'  l_completed_date  eba_sb_responses.completed_date%type;',
'  l_survey_id       eba_sb_surveys.id%type := null;',
'  l_survey_state    eba_sb_surveys.state%type := null;',
'  l_survey_type     eba_sb_surveys.type%type := null;',
'  l_q_id            eba_sb_questionnaires.row_key%type;',
'  l_code            eba_sb_responses.id%type := null;',
'',
'  procedure sendResponse(status varchar2 default ''OK'',',
'                        message varchar2 default '''',',
'                        code varchar2 default null) is',
'  begin',
'    apex_plugin_util.print_json_http_header;',
'    sys.htp.p(''{'' || apex_javascript.add_attribute(''status'', status));',
'    if code is not null then',
'      sys.htp.p(apex_javascript.add_attribute(''code'', code));',
'    end if;',
'    sys.htp.p(apex_javascript.add_attribute(''message'', message, false, false) || ''}'');',
'  end;',
'',
'begin',
'  wwv_flow.debug(''Survey questionnaire start: Questionnaire ID='' || :P99_Q_ID || '', Response ID='' || :P99_RESPONSE_ID);',
'',
'  for c1 in (',
'      select r.id, r.completed_date, s.id survey_id, s.state, s.type, q.row_key',
'        from eba_sb_surveys s join eba_sb_questionnaires q on s.id = q.survey_id left join eba_sb_responses r on s.id = r.survey_id',
'        where q.row_key = :P99_Q_ID and (r.id = :P99_RESPONSE_ID or :P99_RESPONSE_ID is null)) loop',
'    l_response_id := c1.id;',
'    l_completed_date := c1.completed_date;',
'    l_survey_id := c1.survey_id;',
'    l_survey_state := c1.state;',
'    l_survey_type := c1.type;',
'    l_q_id := c1.row_key;',
'    exit; -- when response id is null (self-select survey) there could be more than one result',
'  end loop;',
'',
'  if :P99_RESPONSE_ID is null then',
'    l_response_id := null;',
'  end if;',
'',
'  if l_response_id is null and l_survey_id is not null and l_survey_state in (''TEST'', ''ACTIVE'') and l_survey_type = ''SELF-SELECT'' then',
'    -- this is a response without a response id for a self-select survey so create a response now',
'    l_response_id := eba_sb.add_response(l_survey_id);',
'    l_completed_date := null;',
'    l_code := l_response_id;',
'  end if;',
'',
'  if (l_response_id is null or l_q_id is null or l_survey_id is null) and (:P99_RESPONSE_ID != ''preview'' or :P99_RESPONSE_ID is null) then',
'    -- a response has come in for a response that doesn''t exist or a questionnaire/survey that doesn''t exist',
'    -- or the response and questionnaire exist but are not related to each other.',
'    sendResponse(''FAIL'', apex_lang.message(''INVALID_SURVEY_REQUEST''));',
'    return;',
'  end if;',
'',
'  if l_completed_date is not null then',
'    -- someone is trying to respond to a survey twice',
'    sendResponse(''FAIL'', apex_lang.message(''SURVEY_ALREADY_COMPLETED''));',
'    return;',
'  end if;',
'',
'  if l_survey_state not in (''TEST'', ''ACTIVE'') then',
'    sendResponse(''FAIL'', apex_lang.message(''SURVEY_EXPIRED''));',
'    return;',
'  end if;',
'',
'  -- update response if needed',
'  update eba_sb_responses',
'    set started_date = localtimestamp,',
'      last_question = null',
'    where id = l_response_id and started_date is null;',
'',
'  -- TODO Future gather answers if any',
'',
'  sendResponse(code => l_code);',
'',
'exception ',
'  when others then',
'    sendResponse(''FAIL'', ''Internal error: '' || SQLERRM);',
'end;'))
);
end;
