'        p_respondent_id in number default null',
'    ) is',
'        l_body      clob;',
'        l_body_html clob;',
'        l_subject   varchar2(4000);',
'    begin',
'        l_body := ''To view the content of this message, please use an HTML enabled mail client.''||c_CRLF;',
'        do_substitutions( p_template_id => p_template_id,',
'                          p_subs        => p_subs,',
'                          p_subject     => l_subject,',
'                          p_body        => l_body_html );',
'        apex_mail.send(',
'            p_to        => p_to,',
'            p_from      => p_from,',
'            p_replyto   => p_replyto,',
'            p_cc        => p_cc,',
'            p_bcc       => p_bcc,',
'            p_subj      => l_subject,',
'            p_body      => l_body,',
'            p_body_html => l_body_html',
'        );',
'        apex_mail.push_queue;',
'        if p_log then',
'            -- Store a copy.',
'            insert into eba_sb_email_hist',
'                ( subject, sent_time, sent_by, sent_to, cc, bcc, message, survey_id, community_id, respondent_id )',
'            values (',
'                l_subject,',
'                sysdate,',
'                nvl(v(''APP_USER''), USER),',
'                p_to,',
'                p_cc,',
'                p_bcc,',
'                l_body_html,',
'                p_survey_id,',
'                p_community_id,',
'                p_respondent_id',
'            );',
'        end if;',
'        commit;',
'    end send;',
'end eba_sb_email_api;',
'/',
'show errors',
''))
